import React, { useRef } from "react";
import { useGLTF } from "@react-three/drei";
import { Canvas } from "@react-three/fiber";
import { LineSegments } from "three";
const shapes = require("../../assets/3dModels/backgroundShapes.gltf");

export function Model(props) {
    // destructure shapes and materials from gltf file
    const { nodes, materials } = useGLTF(shapes);

    return (
        <group position={[0, 0, 3]} {...props} dispose={null}>
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube.geometry}
                material={materials["Material.002"]}
                position={[-1.59, 0.8, -0.09]}
                rotation={[-0.26, 0.43, 0.58]}
                scale={0.27}
            />
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube001.geometry}
                material={materials["Material.003"]}
                position={[1.68, 0.22, 0.51]}
                rotation={[0.14, -0.61, 0.24]}
                scale={0.26}
            />
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Roundcube.geometry}
                material={materials["Material.003"]}
                position={[-0.67, -0.78, 0]}
                scale={0.28}
            />
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube002.geometry}
                material={materials["Material.005"]}
                position={[-0.96, -0.59, -0.72]}
                rotation={[-0.31, 0.09, 0.68]}
                scale={[0.63, 0.09, 0.11]}
            >
                <LineSegments
                    position={[0, 0, 0]} // adjust position as needed
                    lineWidth={10} // set desired line width
                >
                    {/* <bufferGeometry attach="geometry" {...nodes.Cube002.geometry} /> */}
                    {/* <meshBasicMaterial attach="material" color="black" /> */}
                </LineSegments>
            </mesh>
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube003.geometry}
                material={materials["Material.003"]}
                position={[-2.08, 0.7, -0.49]}
                rotation={[-0.27, 0.44, 0.58]}
                scale={0.27}
            />
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube004.geometry}
                material={materials["Material.005"]}
                position={[0.23, 0.5, -0.28]}
                rotation={[2.28, -0.63, 2.04]}
                scale={0.18}
            />
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube005.geometry}
                material={materials["Material.004"]}
                position={[0.4, 0.85, -0.12]}
                rotation={[-0.31, -0.05, -0.27]}
                scale={0.27}
            />
            {/* <mesh
                castShadow
                receiveShadow
                geometry={nodes.Cube006.geometry}
                material={materials["Material.004"]}
                position={[1.38, -0.79, -0.42]}
                rotation={[1.72, 0.33, -2.08]}
                scale={0.17}
            /> */}
        </group>
    );
}

export default function Background() {
    const ref = useRef();
    const { nodes } = useGLTF(shapes);

    return (
        <Canvas
            style={{ height: "100%", width: "100%" }}
            camera={{ position: [0, 0, 5], fov: 40 }}
            gl={{ antialias: false }}
        >
            <ambientLight intensity={0.5} />
            <spotLight intensity={0.8} position={[30, 30, 50]} angle={0.2} penumbra={1} />
            <spotLight intensity={0.5} position={[-30, -30, -50]} angle={0.2} penumbra={1} />
            <Model />
        </Canvas>
    );
}
